pragma solidity ^0.5.0;

contract TodoList {
  uint public taskCount = 0;

  struct Task {
    uint id;
    string content;
    bool completed;
  }

  mapping(uint => Task) public tasks;

  event TaskCreated(
    uint id,
    string content,
    bool completed
  );

  event TaskCompleted(
    uint id,
    bool completed
  );

  event TaskUncompleted(
    uint id,
    bool completed
  );

  constructor() public {
    
    createTask("Test Task");
  }

  function createTask(string memory _content) public {
    taskCount ++;
    tasks[taskCount] = Task(taskCount, _content, false);
    emit TaskCreated(taskCount, _content, false);
  }

  function toggleCompleted(uint _id) public {
    Task memory _task = tasks[_id];
    _task.completed = !_task.completed;
    tasks[_id] = _task;
    emit TaskCompleted(_id, _task.completed);
  }


  function toggleUncompleted(uint[] memory _idArray) public {
      uint arrayLength = _idArray.length;
      for (uint i=0; i<arrayLength; i++) {
        Task memory _task = tasks[_idArray[i]];
        _task.completed = !_task.completed;
        tasks[_idArray[i]] = _task;
        emit TaskUncompleted(_idArray[i], _task.completed);
      }  
    }
}
